  },
  {
    id : 4,
    title :"Harry Potter and the Sorcerer's Stone",
    author : "J.K. Rowling",
  },
  {
    id : 5,
    title :"The Hobbit",
    author : "J.R.R. Tolkien",
  },
  {
    id : 6 ,
    title :" Oh, the Places You'll Go!",
    author : "Seuss",
  },
  {
    id : 7,
    title :"The Adventures of Tom Sawyer",
    author : "Mark Twain",
  },]);

  const useEffect=(()=>{
    const getBooks = async ()=>{
      const res = await BooksAPI.getAll();
      console.log(res);
      setBooks(res);
    } ; getBooks();
  }, []); 
  return (<BookTable books={books} onchangestatus={updateStaus}/>);

}


export default App;
