/* eslint-disable react/jsx-no-comment-textnodes */
import { useState } from "react";
const BookTable = ({books,onchangestatus,selected}) => 
{ console.log(books);
  const [showSearchPage, setShowSearchpage] = useState(null);
  const searchQuery =(showSearchPage)=>{
    setShowSearchpage(showSearchPage.trim());
  };
   
  const options = [{
    value : 'None' , text : 'None',
},
   { value : 'Currently Reading' , text : 'Currently Reading',
},{
    value : 'Want to Read' , text : 'Want to Read',
},{
    value : 'Read' , text : 'Read',
},];


